document.addEventListener('DOMContentLoaded',function(){const imageModal=document.getElementById('imageModal');if(imageModal){imageModal.addEventListener('show.bs.modal',function(event){const openerLink=event.relatedTarget;const largeSrc=openerLink.getAttribute('data-large-src');const altText=openerLink.querySelector('img').alt;const modalImage=imageModal.querySelector('#modalImage');const modalTitle=imageModal.querySelector('.modal-title');modalImage.src=largeSrc;modalImage.alt=altText;modalTitle.textContent=altText;});}});