document.addEventListener('DOMContentLoaded',function(){let headerPath='includes/header.html';let footerPath='includes/footer.html';const currentPath=window.location.pathname;if(currentPath.includes('/static_rooms/')||currentPath.endsWith('/rooms.html')||currentPath.endsWith('/contact.html')){headerPath='../includes/header.html';footerPath='../includes/footer.html';}
const headerPlaceholder=document.getElementById('header-placeholder');if(headerPlaceholder){fetch(headerPath).then(response=>response.text()).then(data=>{headerPlaceholder.innerHTML=data;}).catch(error=>console.error('Error loading header:',error));}
const footerPlaceholder=document.getElementById('footer-placeholder');if(footerPlaceholder){fetch(footerPath).then(response=>response.text()).then(data=>{footerPlaceholder.innerHTML=data;}).catch(error=>console.error('Error loading footer:',error));}
const imageModal=document.getElementById('imageModal');if(imageModal){imageModal.addEventListener('show.bs.modal',function(event){const openerLink=event.relatedTarget;const largeSrc=openerLink.getAttribute('data-large-src');const altText=openerLink.querySelector('img').alt;const modalImage=imageModal.querySelector('#modalImage');const modalTitle=imageModal.querySelector('.modal-title');modalImage.src=largeSrc;modalImage.alt=altText;modalTitle.textContent=altText;});}});